mapper.update:
UPDATE board
SET
       board_title = #{btitle},
       board_content = #{bcontent}
WHERE  board_no = #{bno}

UPDATE board
SET    board_hit  = board_hit +1
WHERE  board_no = #{bno}

mapper.select:
SELECT board_heart AS bheart
FROM   board
WHERE  board_no = #{bno}

mapper.update:
UPDATE board
SET    board_heart = board_heart+1
WHERE  board_no = #{bno}

UPDATE  board
SET     board_heart = board_heart-1
WHERE   board_no = #{bno}
