mapper.select:
SELECT COUNT(*)
FROM   member
WHERE  id = #{id}

mapper.insert:
INSERT INTO authority ( username, auth ) VALUES
(#{username}, #{authority.role})
ON DUPLICATE KEY UPDATE username = #{username}

mapper.select:
SELECT username as username
FROM  member
WHERE name = #{name}
AND pnum = #{pnum}

SELECT COUNT(*)
FROM member
WHERE id = #{id}
AND   name = #{name}

mapper.update:
UPDATE member
SET
       password = #{password}
WHERE  id = #{id}
AND    name = #{name}

mapper.insert:
INSERT INTO emailauth (
    email,
    authkey
) VALUES (
    #{email},
    #{authkey}
)
